import { message } from 'antd'

//tasks
export const fetchFilms = (page, sort_field, sort_direction) => dispatch => {
  dispatch({
    type: 'START_LOAD_FILMS', payload: {
      page: page,
      sort_field: sort_field,
      sort_direction: sort_direction
    }
  })
  fetch(`/api/film/?page=${page}&sort_field=${sort_field}&sort_direction=${sort_direction}`)
    .then(res => res.json())
    .then(data => {
      setTimeout(() => {
        if (data.status) {
          dispatch({
            type: 'SUCCESS_LOAD_FILMS',
            payload: {
              list: data.message.result,
              count: Number(data.message.count),
            }
          })
        } else {
          message.error('Error Load Data!')
          dispatch({ type: 'FAILED_LOAD_FILMS' })
        }
      }, 500)
    })
    .catch(err => {
      console.log(err)
      message.error('Error Load Data!')
      dispatch({ type: 'FAILED_LOAD_FILMS' })
    })
}

export const fetchFilm = id => dispatch => {
  dispatch({ type: 'START_LOAD_FILM' })
  fetch(`/api/film/${id}`)
    .then(res => res.json())
    .then(data => {
      setTimeout(() => {
        if (data.status) {
          dispatch({
            type: 'SUCCESS_LOAD_FILM',
            payload: data.message
          })
        } else {
          message.error('Error Load Data!')
          dispatch({ type: 'FAILED_LOAD_FILM' })
        }
      }, 500)
    })
    .catch(err => {
      console.log(err)
      message.error('Error Load Data!')
      dispatch({ type: 'FAILED_LOAD_FILM' })
    })
}

export const filmChangeName = e => ({ type: 'CHANGE_NAME', payload: e.target.value })
export const filmChangeDate = value => {
  console.log(value)
    return ({ type: 'CHANGE_DATE', payload: value.valueOf() })
}
export const filmChangeFormat = value => ({ type: 'CHANGE_FORMAT', payload: value })
export const filmChangeTag = value => ({ type: 'CHANGE_TAG', payload: value })

export const addTasks = values => dispatch => {
  let data = new FormData()
  data.append('username', values.username)
  data.append('email', values.email)
  data.append('text', values.text)

  fetch('https://uxcandy.com/~shapoval/test-task-backend/v2/create/?developer=vito', {
    method: 'POST',
    body: data,
  })
    .then(res => res.json())
    .then(data => {
      if (data.status === 'ok') {
        dispatch({ type: 'ADD_TASKS', payload: data.message })
        message.success('Success Create Task!')
      } else {
        message.error('Error Create Task!')
        dispatch({ type: 'FAILED_LOAD_TASKS' })
      }
    })
    .catch(err => {
      console.log(err)
      message.error('Error Create Task!')
      dispatch({ type: 'FAILED_LOAD_TASKS' })
    })
}

export const editTasks = (values, id, token) => dispatch => {
  let data = new FormData()
  values.status = values.status ? 10 : 0
  data.append('status', values.status)
  data.append('text', values.text)
  data.append('token', token)

  fetch(`https://uxcandy.com/~shapoval/test-task-backend/v2/edit/${id}/?developer=vito`, {
    method: 'POST',
    body: data,
  })
    .then(res => res.json())
    .then(data => {
      if (data.status === 'ok') {
        dispatch({
          type: 'EDIT_TASKS', payload: {
            data: values,
            id: id
          }
        })
        message.success('Success Update Task!')
      } else {
        if (data.message.hasOwnProperty('token')) {
          /*notification['error']({
            message: 'Error Update Task',
            description: data.message.token,
          })*/
          dispatch({ type: 'AUTH_LOGOUT' })
          dispatch({ type: 'FAILED_LOAD_TASKS' })
        } else {
          message.error('Error Update Task!')
          dispatch({ type: 'FAILED_LOAD_TASKS' })
        }
      }
    })
    .catch(err => {
      console.log(err)
      message.error('Error Update Task!')
      dispatch({ type: 'FAILED_LOAD_TASKS' })
    })
}
//modal
export const openModal = () => ({ type: 'OPEN_MODAL' })
export const openUpdate = id => ({ type: 'OPEN_UPDATE', payload: id })
export const closeModal = () => ({ type: 'CLOSE_MODAL' })
export const closeUpdate = () => ({ type: 'CLOSE_UPDATE' })
