import React from 'react'
import { Modal, Form, Input } from 'antd'
import { connect } from 'react-redux'
import { bindActionCreators } from 'redux'
//action
import { closeModal } from '../store/action'

const ModalForm = ({ modal, closeModal, form, handleCreate }) => {
    const { getFieldDecorator } = form
    return (
        <Modal
            visible={modal}
            title="Create a new Task"
            okText="Create"
            onCancel={closeModal}
            onOk={()=>handleCreate(form)}
        >
            <Form layout="vertical">
                <Form.Item label="Username">
                    {getFieldDecorator('username', {
                        rules: [{ required: true, message: 'Please input the Username!' }],
                    })(<Input />)}
                </Form.Item>
                <Form.Item label="Email">
                    {getFieldDecorator('email', {
                        rules: [{ required: true, message: 'Please input the Email!' }, { type: 'email', message: 'The input is not valid E-mail!', },],
                    })(<Input type="email" />)}
                </Form.Item>
                <Form.Item label="Text">
                    {getFieldDecorator('text', {
                        rules: [{ required: true, message: 'Please input the Text!' }],
                    })(<Input />)}
                </Form.Item>
            </Form>
        </Modal>
    )
}

const _ModalForm = Form.create({ name: 'form_in_modal' })(ModalForm)

const mapStateToProps = state => ({
    modal: state.modal.add.visible
})
const mapDispatchToProps = dispatch => ({
    closeModal: bindActionCreators(closeModal, dispatch),
})

export default connect(mapStateToProps, mapDispatchToProps)(_ModalForm)
