import React from 'react'
import { Result, Button, Icon } from 'antd'
import { Link } from "react-router-dom"

const Page404 = () =>
    <React.Fragment>
        <FileInput />
        <Result
            status="404"
            title="404"
            subTitle="Sorry, the page you visited does not exist."
            extra={<Button type="primary"><Link to='/'><Icon type="video-camera" /> Films</Link></Button>}
        />
    </React.Fragment>

export default Page404

class FileInput extends React.Component {
    constructor(props) {
        super(props)
        this.handleSubmit = this.handleSubmit.bind(this)
        this.fileInput = React.createRef()
    }
    handleSubmit(event) {
        event.preventDefault()
        let reader = new FileReader()
        reader.readAsText(this.fileInput.current.files[0])
        reader.onload = (e) => {
            let result = e.target.result
            result = result.split('\n').filter(el => el !== '')
            let data = []
            for (let i = 0; i < result.length; i += 4) {
                let name = result[i].slice(7)
                let release = result[i + 1].slice(14)
                let format = result[i + 2].slice(8)
                let actors = result[i + 3].slice(7).split(',').map(el => el.trim()).map(el => {
                    let elements = el.split(' ')
                    return {
                        f_name: elements[0],
                        l_name: elements[1]
                    }
                })

                data.push({ name, release, format, actors })
            }
            console.log(data)
        }
    }
    render() {
        return (
            <form onSubmit={this.handleSubmit}>
                <label>
                    Upload file:
            <input type="file" ref={this.fileInput} name="file" />
                </label>
                <br />
                <button type="submit">Submit</button>
            </form>
        );
    }
}
