export const initialState = {
    films: {
        list: [],
        count: 0,
        page: 1,
        sort_field: 'name',
        sort_direction: 'asc',
        load: false,
    },
    film: {
        data: {},
        load:false,
    }
}

export function reduser(state = initialState, action) {
    switch (action.type) {
        //tasks
        case 'START_LOAD_FILMS':
            return { ...state, films: { ...state.films, load: true, page: action.payload.page, sort_field: action.payload.sort_field, sort_direction: action.payload.sort_direction } }
        case 'FAILED_LOAD_FILMS':
            return { ...state, films: { ...state.films, load: false } }
        case 'SUCCESS_LOAD_FILMS':
            return { ...state, films: { ...state.films, load: false, list: action.payload.list, count: action.payload.count } }
        case 'ADD_TASKS':
            return { ...state, tasks: { ...state.tasks, arr: [...state.tasks.arr, action.payload] } }
        case 'EDIT_TASKS': {
            let _arr = [...state.tasks.arr]
            _arr.map(el => {
                if (el.id === action.payload.id) {
                    el.status = action.payload.data.status
                    el.text = action.payload.data.text
                }
                return el
            })
            return { ...state, tasks: { ...state.tasks, arr: _arr } }
        }
        //modal
        case 'OPEN_MODAL':
            return { ...state, modal: { ...state.modal, add: { ...state.modal.add, visible: true } } }
        case 'CLOSE_MODAL':
            return { ...state, modal: { ...state.modal, add: { ...state.modal.add, visible: false } } }
        case 'OPEN_UPDATE': {
            let id = action.payload
            let status = ''
            let text = ''
            state.tasks.arr.map(el => {
                if (el.id === id) {
                    status = el.status
                    text = el.text
                }
                return el
            })
            return { ...state, modal: { ...state.modal, update: { ...state.modal.update, visible: true, status: status, text: text, id: id } } }
        }

        case 'CLOSE_UPDATE':
            return { ...state, modal: { ...state.modal, update: { ...state.modal.update, visible: false, status: '', text: '', id: '' } } }
        default:
            return state;
    }
}
