import React from 'react'
import { List, Skeleton, Button } from 'antd'
import { connect } from 'react-redux'
import { bindActionCreators } from 'redux'
//action
import { fetchTasks, openModal, closeModal, addTasks, editTasks, openUpdate, closeUpdate } from '../store/action'
//component
import TasksBreadcrumb from '../components/breadcrumb'
import ListPagination from '../components/pagination'
import ListSort from '../components/sort'
import ModalFormAdd from '../components/modalFormAdd'
import ModalFormUpdate from '../components/modalFormUpdate'

let breadcrumbs = [
    {
        path: '/',
        breadcrumbName: 'Home',
    },
    {
        breadcrumbName: 'Tasks',
    }
]

class Tasks extends React.Component {
    constructor(props){
        super(props)
        this.handleUpdate=this.handleUpdate.bind(this)
        this.handleCreate=this.handleCreate.bind(this)
        this.openUpdate=this.openUpdate.bind(this)
    }
    componentDidMount() {
        //this.props.fetchTasks(1, 'id', 'asc')
    }
    componentWillReceiveProps(nextProps) {
        //if (nextProps.arr.length !== this.props.arr.length) this.props.fetchTasks(this.props.page, this.props.sort_field, this.props.sort_direction)
    }
    handleCreate = form => {
        form.validateFields((err, values) => {
            if (err) {
                return;
            }
            form.resetFields()
            this.props.closeModal()
            this.props.addTasks(values)
        })
    }
    handleUpdate = (form, id) => {
        form.validateFields((err, values) => {
            if (err) {
                return;
            }
            form.resetFields()
            this.props.closeUpdate()
            this.props.editTasks(values, id, this.props.auth.token)
        })
    }
    openUpdate(id) {
        this.props.openUpdate(id)
    }
    render() {
        let { auth, count, load, arr, openModal } = this.props
        return (
            <React.Fragment>
                <TasksBreadcrumb breadcrumbs={breadcrumbs} />
                <div className='list-body'>
                    {
                        !load
                            ?
                            <TasksList arr={arr} count={count} auth={auth} openModal={openModal} openUpdate={this.openUpdate} />
                            :
                            <LoadData size={3} />
                    }
                </div>
                <ModalFormUpdate handleUpdate={this.handleUpdate} />
                <ModalFormAdd handleCreate={this.handleCreate} />
            </React.Fragment>
        )
    }
}

const mapStateToProps = state => ({
    count: state.films.count,
    page: state.films.page,
    sort_field: state.films.sort_field,
    sort_direction: state.films.sort_direction,
    load: state.films.load,
    list: state.films.list,
})
const mapDispatchToProps = dispatch => ({
    /*fetchTasks: bindActionCreators(fetchTasks, dispatch),
    openModal: bindActionCreators(openModal, dispatch),
    openUpdate: bindActionCreators(openUpdate, dispatch),
    closeModal: bindActionCreators(closeModal, dispatch),
    closeUpdate: bindActionCreators(closeUpdate, dispatch),
    addTasks: bindActionCreators(addTasks, dispatch),
    editTasks: bindActionCreators(editTasks, dispatch),*/
})

export default connect(mapStateToProps, mapDispatchToProps)(Tasks)

//helper
//skeleton
const LoadData = ({ size }) => {
    let list = []
    for (let i = 0; i < size; i++) list.push(<Skeleton title={false} loading={true} active key={i}></Skeleton>)
    return list
}
//List
const TasksList = ({ arr, count, auth, openModal, openUpdate }) =>
    <div>
        <Button type="primary" shape="circle" icon="user-add" size='large' className='add-user-btn' onClick={openModal} />
        {
            count
                ?
                <ListSort />
                :
                ""
        }
        <List
            className="demo-loadmore-list"
            itemLayout="horizontal"
            dataSource={arr}
            renderItem={item => <ListElement data={item} key={item.id} auth={auth} openUpdate={openUpdate} number />}
        />
        {
            count > 3
                ?
                <ListPagination />
                : ''
        }
    </div>
//ListElement
const ListElement = ({ data, auth, openUpdate }) => {
    return (
        <React.Fragment>
            <List.Item
                actions={auth.status ? [<a key="list-loadmore-more" href='#' onClick={()=>openUpdate(data.id)}>edit</a>] : []}
                className={data.status === 10 ? 'item-list-done' : ''}
            >
                <List.Item.Meta
                    title={'Name: ' + data.username}
                    description={'Email: ' + data.email}

                />
                {'Text: ' + data.text}
            </List.Item>
        </React.Fragment>
    )
}
