import React from 'react'
import { Card, Icon, Popconfirm, Input, InputNumber, Select, Result } from 'antd'
import { connect } from 'react-redux'
import { bindActionCreators } from 'redux'
//action
/*import {
    fetchWorker,
    fetchDepartments,
    WorkerChangeName,
    WorkerChangeAge,
    WorkerChangePhones,
    WorkerChangeEmails,
    WorkerChangeDepartment,
    deleteWorker,
    updateWorker
} from '../store/action'*/
//component
import WorkersBreadcrumb from '../components/breadcrumb'

let breadcrumb = [
    {
        path: '/',
        breadcrumbName: 'Home',
    },
    {
        path: '/workers/',
        breadcrumbName: 'Workers',
    }
]

class Task extends React.Component {
    componentDidMount() {

    }
    render() {
        let { load, data } = this.props
        let breadcrumbs = [...breadcrumb, { breadcrumbName: load ? '...' : data.name ? data.name : 'Noname' }]
        return (
            <React.Fragment>
                <WorkersBreadcrumb breadcrumbs={breadcrumbs} />
                {
                    load
                        ?
                        <LoadCard />
                        :
                        <RenderCard data={data}
                            changeName={this.props.WorkerChangeName}
                            changeAge={this.props.WorkerChangeAge}
                            changePhones={this.props.WorkerChangePhones}
                            changeEmails={this.props.WorkerChangeEmails}
                            changeDepartment={this.props.WorkerChangeDepartment}
                            deleteWorker={this.deleteWorker}
                            updateWorker={this.updateWorker}
                        />
                }
            </React.Fragment>
        )
    }
}

const mapStateToProps = state => ({
    load: state.film.load,
    data: state.film.data
})
const mapDispatchToProps = dispatch => ({
    // fetchFilm: bindActionCreators(fetchFilm, dispatch),
})

export default connect(mapStateToProps, mapDispatchToProps)(Task)

const LoadCard = () =>
    <Card style={{ width: 300, margin: '20px auto' }} loading={true}
        actions={[
            <Popconfirm
                disabled
                key='save'
            >
                <Icon type="save" key='save' />
            </Popconfirm>,
            <Popconfirm
                disabled
                key='delete'
            >
                <Icon type="delete" />
            </Popconfirm>
        ]}>
    </Card>
